import fetch from 'isomorphic-unfetch'

import React from 'react';

HomePage.getInitialProps = async ({ req, query }) => {
    const pageRequest = `${"http:"}//${"localhost:3000"}/api/profiles?limit=10`
    const res = await fetch(pageRequest)
    const json = await res.json()
    return json
};

function HomePage(ctx) {
    console.log(ctx)
    return (
        <>
            <h1> Political Tweeter Prototype V1! </h1>
            <h2> Candidates </h2>
            {ctx.tweets.map(post => (
                <li key={post.candidate_id}>
                    <a>#{post.candidate_id} - {post.candidate_name} - {post.party}</a>
                </li>
                ))}
            <style jsx>{`
        h1,
        a {
          font-family: 'Arial';
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
        </>
    )
}

export default HomePage
