import fetch from 'isomorphic-unfetch'
import Link from 'next/link'

HomePage.getInitialProps = async ctx => {
    const protocol = req
        ? `${ctx.req.headers['x-forwarded-proto']}:`
        : location.protocol;

    const host = ctx.req ? ctx.req.headers['x-forwarded-host'] : location.host;
    const pageRequest = `${protocol}//${host}/api/profiles?page=${query.page ||
    1}&limit=${ctx.query.limit || 9}`;
    console.log(pageRequest);
    const res = await fetch(pageRequest);
    const json = await res.json();
    return json
}

function HomePage({ profiles, page, pageCount }) {
    return (
        <>
            <ul>
                {profiles.map(p => (
                    <li className="profile" key={p.id}>
                        <Link href={`/profile?id=${p.id}`}>
                            <a>
                                <span>{p.name}</span>
                            </a>
                        </Link>
                    </li>
                ))}
            </ul>
            <nav>
                {page > 1 && (
                    <Link href={`/?page=${page - 1}&limit=9`}>
                        <a>Previous</a>
                    </Link>
                )}
                {page < pageCount && (
                    <Link href={`/?page=${page + 1}&limit=9`}>
                        <a className="next">Next</a>
                    </Link>
                )}
            </nav>
        </>
    )
}

export default HomePage
