const mysql = require('serverless-mysql');

const db = mysql({
    config: {
        host: "localhost",
        database: "sql_tweet",
        user: "root",
        password: "T3chN3rd1979@!"
    }
});

exports.query = async query => {
    try {
        console.log('Querying DB...');
        const results = await db.query(query);
        await db.end();
        return results
    } catch (error) {
        return { error }
    }
};
